enum UpgradeableLoaderState {
    Uninitialized,
    Buffer {
            authority_address: Option<Pubkey>,
        },
    Program {
            programdata_address: Pubkey,
        },
    ProgramData {
            slot: u64,
            upgrade_authority_address: Option<Pubkey>,
        },
}

for<'_, '_, '_,
    '_> fn(program_id: &'_ Pubkey, accounts: &'_ [AccountInfo],
    instruction_data: &'_ [u8]) -> ProgramResult

for<'_, '_, '_,
    '_> fn(program_id: &'_ Pubkey, accounts: &'_ [AccountInfo],
    instruction_data: &'_ [u8]) -> ProgramResult

enum LoaderInstruction {
    Write {
            offset: u32,
            bytes: Vec<u8>,
        },
    Finalize,
}

enum UpgradeableLoaderInstruction {
    InitializeBuffer,
    Write {
            offset: u32,
            bytes: Vec<u8>,
        },
    DeployWithMaxDataLen {
            max_data_len: usize,
        },
    Upgrade,
    SetAuthority,
    Close,
    ExtendProgramData {
            additional_bytes: u32,
        },
}

enum StakeState {
    Uninitialized,
    Initialized(Meta),
    Stake(Meta, Stake),
    RewardsPool,
}

enum NonceError {
    NoRecentBlockhashes,
    NotExpired,
    UnexpectedValue,
    BadAccountState,
}

enum SystemInstruction {
    CreateAccount {
            lamports: u64,
            space: u64,
            owner: Pubkey,
        },
    Assign {
            owner: Pubkey,
        },
    Transfer {
            lamports: u64,
        },
    CreateAccountWithSeed {
            base: Pubkey,
            seed: String,
            lamports: u64,
            space: u64,
            owner: Pubkey,
        },
    AdvanceNonceAccount,
    WithdrawNonceAccount(u64),
    InitializeNonceAccount(Pubkey),
    AuthorizeNonceAccount(Pubkey),
    Allocate {
            space: u64,
        },
    AllocateWithSeed {
            base: Pubkey,
            seed: String,
            space: u64,
            owner: Pubkey,
        },
    AssignWithSeed {
            base: Pubkey,
            seed: String,
            owner: Pubkey,
        },
    TransferWithSeed {
            lamports: u64,
            from_seed: String,
            from_owner: Pubkey,
        },
    UpgradeNonceAccount,
}
